        <div className="post">
            <div className="post__header">
                <Avatar
                    className="post__avatar"
                    alt="RafeHQazi"
                    src="@material-ui/core/avatar/1.jpg"
                />
                <h3>{username}</h3>
            </div>


            {/* header -> avatar + username */}

            <img 
                className="post__image"
                src={imageUrl}/>

        <h4 className="post__text"><strong>{username}</strong> {caption}</h4>
        </div>
    )
}

export default Post
